!function() {
    "use strict";
    function myDirective() {
        return {
            restrict: "E",
            scope: {
                model: "=ngModel"
            },
            controller: function() {
                var vm = this;
                vm.test = "Directive";
            },
            controllerAs: "vm",
            template: "<p>Rendered by {{vm.test}}</p>"
        };
    }
    angular.module("my-application.directives").directive("myDirective", myDirective);
}();