!function() {
    "use strict";
    function MyResourceService($q) {
        function get(id) {
            return self.list().then(function(resources) {
                var val = resources[0];
                return angular.forEach(resources, function(resource) {
                    id === resource.id && (val = resource);
                }), val;
            });
        }
        function list() {
            var deferred = $q.defer();
            return deferred.resolve([ {
                id: 1,
                name: "Hello World!"
            }, {
                id: 2,
                name: "Is anybody out there?"
            } ]), deferred.promise;
        }
        function save(resource) {
            console.log("Saving resource...");
            var deferred = $q.defer();
            return deferred.resolve(resource), deferred.promise;
        }
        var self = this;
        self.get = get, self.list = list, self.save = save;
    }
    angular.module("my-application.services").service("MyResourceService", MyResourceService), 
    MyResourceService.$inject = [ "$q" ];
}();